function App() {

const [navbarOpen, setNavbarOpen] = useState(false);
  const [animateHeader, setAnimateHeader] = useState(false);
  const pathname = location.pathname;
  useEffect(() => {
    const listener = () => {
      if (window.scrollY > 100) {
        setAnimateHeader(true);
      } else setAnimateHeader(false);
    };
    window.addEventListener("scroll", listener);

    return () => {
      window.removeEventListener("scroll", listener);
    };
  }, []);

  return (
    <div className="w-full min-h-screen flex flex-col mx-auto items-center font-poppins">
      {/* Header */}
      <nav
        className={`w-full bg-white font-poppinsSemibold fixed z-10 trasition ease-in-out duration-100 ${
          pathname === "/" ? (animateHeader ? "border border-b-2" : "border-0") : "border border-b-2"
        }`}
      >
        <div className="justify-between mx-auto md:items-center md:flex md:px-8 ">
          <div>
            <div className="flex items-center justify-between py-3 px-3 md:py-3 md:block">
              <div className="flex">
                {/* <Link to="/"> */}
                  <button className="flex" onClick={() => {}}>
                    <img className="h-8" fill="black" src={Logo} alt="Workflow" />
                    <span className="text-[#7CBD1E] font-bold text-2xl pl-2 font-poppins">TANDUR</span>
                  </button>
                {/* </Link> */}
              </div>
              <div className="md:hidden">
                <button
                  className="p-2 text-gray-700 rounded-md outline-none focus:border-gray-400 focus:border"
                  onClick={() => setNavbarOpen(!navbarOpen)}
                >
                  {navbarOpen ? (
                    <svg xmlns="http://www.w3.org/2000/svg" className="w-6 h-6 text-black" viewBox="0 0 20 20" fill="currentColor">
                      <path
                        fillRule="evenodd"
                        d="M4.293 4.293a1 1 0 011.414 0L10 8.586l4.293-4.293a1 1 0 111.414 1.414L11.414 10l4.293 4.293a1 1 0 01-1.414 1.414L10 11.414l-4.293 4.293a1 1 0 01-1.414-1.414L8.586 10 4.293 5.707a1 1 0 010-1.414z"
                        clipRule="evenodd"
                      />
                    </svg>
                  ) : (
                    <svg
                      xmlns="http://www.w3.org/2000/svg"
                      className="w-6 h-6 text-black"
                      fill="none"
                      viewBox="0 0 24 24"
                      stroke="currentColor"
                      strokeWidth={2}
                    >
                      <path strokeLinecap="round" strokeLinejoin="round" d="M4 6h16M4 12h16M4 18h16" />
                    </svg>
                  )}
                </button>
              </div>
            </div>
          </div>
          <div>
            <div className={`flex-1 justify-self-center pb-3 mt-8 md:block md:pb-0 md:mt-0 ${navbarOpen ? "block mx-5" : "hidden"}`}>
              <ul className={`items-center justify-center space-y-8 md:flex md:space-x-6 md:space-y-0 ${props.header && "invisible"}`}>
                <li className="text-font-primary font-poppinsSemibold text-lg hover:text-gray-900">
                  <button onClick={() => {}}>Urban Farming</button>
                </li>
                <li className="text-font-primary font-poppinsSemibold text-lg hover:text-gray-900">
                  <button onClick={() => {}}>Tandur Market</button>
                </li>
                <li className="text-font-primary font-poppinsSemibold text-lg hover:text-gray-900">
                  <button onClick={() => {}}>Belajar Nandur</button>
                </li>
              </ul>
              <div className="md:hidden md:flex-col justify-center border-t-2 border-t-gray-300 my-5 w-full self-center" />

              <div className="mt-3 space-y-2 md:hidden sm:inline-block">
                {/* <Link to="/masuk"> */}
                <button className="px-4 py-1 text-black border-gray-600 border-2 rounded-md shadow hover:bg-gray-200">Masuk</button>
                {/* </Link> */}
              </div>
            </div>
          </div>
          <div className="hidden space-x-2 md:inline-block">
            {/* <Link to="/masuk"> */}
            <button className="px-4 py-1 text-black border-gray-600 border-2 rounded-md shadow hover:bg-gray-200">Masuk</button>
            {/* </Link> */}
          </div>
        </div>
      </nav>
      <h1 className="text-3xl font-bold underline">Hello world!</h1>
    </div>
  );
}

export default App;
